
import 'package:sqflite/sqflite.dart';
import 'package:sqflite_common_ffi/sqflite_ffi.dart';
import 'package:sqflite_common_ffi_web/sqflite_ffi_web.dart';
import 'dart:typed_data';
import 'dart:convert';
import 'package:path/path.dart';
import 'package:flutter/foundation.dart' show kIsWeb; // Pour détecter la plateforme

class DatabaseService {
  static final DatabaseService _instance = DatabaseService._internal();
  factory DatabaseService() => _instance;

  DatabaseService._internal();

  static Database? _database;

  Future<Database?> get database async {
    if (_database != null) return _database;
    _database = await _initDatabase();
    return _database;
  }
 
   Future<Database> _initDatabase() async {
    if (kIsWeb) {
      databaseFactory = databaseFactoryFfiWeb;
    } else {
      String path = await getDatabasesPath(); // chemin pour IOS
      String dbPath = join(path, 'historique.db');
      return await openDatabase(
        dbPath,
        version: 1,
        onCreate: _onCreate,
      );
    }
    throw UnimplementedError("Platform not supported");
  }


  Future<void> _onCreate(Database db, int version) async {
    await db.execute('''
      CREATE TABLE historique (
        id INTEGER PRIMARY KEY,
        name TEXT,
        latitude REAL,
        longitude REAL,
        prediction_score REAL,
        image BLOB,
        url TEXT,
        timestamp TEXT
      )
    ''');

    await _insertInitialData(db);
  }

  Future<void> _insertInitialData(Database db) async {
    // Image encodée en Base64 pour l'exemple
    String base64Image = '';

    final Uint8List imageBytes = base64Decode(base64Image);

    await db.insert(
      'historique',
      {
        'name': 'Place Example',
        'latitude': 34.0522,
        'longitude': -118.2437,
        'prediction_score': 85.6,
        'image': imageBytes,
        'url': 'http://example.com',
        'timestamp': DateTime.now().toIso8601String(),
      },
      conflictAlgorithm: ConflictAlgorithm.replace,
    );
  }
   Future<List<Map<String, dynamic>>> getAllHistorique() async {
    Database? db = await database;
    return await db!.query('historique');
  }
}
